import React from "react"
import PropTypes from "prop-types"

function handleClick(a){
  setState ({
    sportId: a,
  });
}
const json = require('./overview.json');
let rows = []

for (let i = 0; i < json['slider_elements'].length; i++) {
    rows.push(<div key={i} > <h3> {json['slider_elements'][i]['desc']} </h3>
      <a href={"/sport/" + json['slider_elements'][i]['id']} 
      className='goToEvents' onClick={() => {handleClick(json['slider_elements'][i]['id'])}}> events </a>
     </div>);
}


class Sports extends React.Component {
  constructor(props){
      super(props);
      this.state = {
           sportId: null
      }
  }
  reqListener(e) {
    console.log(json['slider_elements'][0]);
  } 
  render () {
    return (
      <React.Fragment>
        <h1>Sports </h1>
        <div> {rows} </div>

      </React.Fragment>
    );
  }
}

export default Sports
