import React from "react"
import PropTypes from "prop-types"
import { BrowserRouter, Switch, Route } from 'react-router-dom'

import HelloWorld from "./HelloWorld"
import Sports from "./Sports"
import Events from "./Events"


class App extends React.Component {
  constructor(props){
    super(props);
    this.sportId = ""
  }
  myCallback(dataFromChild) {
    setState({ sportId: dataFromChild });
    console.log(sportId)
  }
  render () {
    return (
		<BrowserRouter>
			<Switch>
				<Route exact path="/" render={() => ("Home!")} />      
      	<Route path="/hello" render={() => <HelloWorld greeting="friend, go to "/>} />
        <Route path="/sport/*" render={() => <Events test={this.sportId} />} /> 
        <Route path="/sports" render={() => <Sports callbackFromParent={this.myCallback}/>} />  
    	</Switch>
    </BrowserRouter>
    );	
  }
}

export default App
