import React from "react"
import PropTypes from "prop-types"

const handleClick = (e) => {
  e.preventDefault();
  console.log('The link was clicked.');
};

const json = require('./overview.json');
const url = this.props.location.pathname;
let rows = []

const SubEvent = (props)  => {
  for (let i = 0; i < json['sports'].length; i++) {
    if (json['sports'][i]['id'] == parseInt(url.split('/',5)[4])){
      if (json['sports'][i]['comp'].length != 0){
        console.log(json['sports'][i]['comp'][0]['desc'])
        rows.push(<div key={i} > <h3> {json['sports'][i]['comp'][0]['desc']} </h3>
       </div>);
       } else{
        rows.push(<div key={i} > <h3> No Events </h3>
       </div>);
       }
      }
  }
  return rows
}


class Events extends React.Component {
  reqListener(e) {
    console.log(json['slider_elements'][0]);
  } 
  render () {
    return (
      <React.Fragment>
        <h1>Events </h1>  
        <div> <SubEvent display={true} /> </div>
      </React.Fragment>
    );
  }
}

export default Events
